clearformat longclcclose all#arquivo de entradaXYZ = load('C:\Users\josec\Desktop\mapa\TCCbom2.txt');X = XYZ(:,1);Y = XYZ(:,2);Z = XYZ(:,3);#####################################################################################################################################qual o temanho do vetorxt=length(X)#menor valor do vetor xmin=min(X)#maior valor do vetorxmax=max(X)#media xmed=mean(X)# diz quantas vezes foi registrado valores acima de xmedfortes = X( X > xmed);Vacx=length(fortes)AmplitudeX=xmax-xmin#########################################################################################################################################qual o temanho do vetoryt=length(Y)#menor valor do vetor ymin=min(Y)#maior valor do vetorymax=max(Y)#media ymed=mean(Y)# diz quantas vezes foi registrado valores acima de ymedfortes= Y(Y > ymed);Vacy=length(fortes)AmplitudeY=ymax-ymin#########################################################################################################################################qual o temanho do vetor zt=length(Z)#menor valor do vetor  zmin=min(Z)#maior valor do vetor zmax=max(Z)#media zmed=mean(Z) #diz quantas vezes foi registrado valores acima de zmedfortes = Z(Z > zmed);Vacz=length(fortes) AmplitudeZ=zmax-zmin#diz a % de pontos que estão acima da média  Zp=(Vacz/zt)zpp=Zp*100#arquivo de saídasave ('C:\Users\josec\Desktop\mapa\relatorio.txt', "xt","xmin","xmax","xmed","Vacx","AmplitudeX","yt","ymin","ymax","ymed","Vacy","AmplitudeY","zt","zmin","zmax","zmed","Vacz","AmplitudeZ","Zp");###################################################################################################################################################################Graficos##########################################################################################################X######y#######################################################